import React, { useState, useEffect, useRef } from 'react'
import Header from './Header'
import { stringify, v4 as uuidv4 } from 'uuid';
import Inventory from './Inventory';
import Catalog from './Catalog';

const LOCAL_STORAGE_KEY = 'itemsDB'

function App() {
  const [items, setItems] = useState([])
  const [page, setPage] = useState('i')
  const addIDRef = useRef()

  useEffect(() => {
    const storedItems = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY))
    if (storedItems) setItems(storedItems)
  }, [])

  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(items))
  }, [items])

  console.log(require("./data/catalog.json")["asd"])

  function handleAddItem() {
    const id = addIDRef.current.value
    if (id === '') return
    addIDRef.current.value = null
    setItems(prevIDs => {
      return [...prevIDs, {id: id}]
    })
  }

  function handleClearItems() {
    setItems([])
  }

  function showInventory() {
    setPage('i')
  }

  function showCatalog(){
    setPage('c')
  }

  return (
    <>
    <header>
      <Header showInventory={showInventory} showCatalog={showCatalog}/>
    </header>
    {page === 'i' ? <Inventory /> : <Catalog />}
    </>
  )
}

export default App;
